/**
 * `./tsconfig.json` updater.
 *
 * @note PLEASE DO NOT EDIT THIS FILE!
 * @note This entire file will be updated automatically.
 * @note Instead of editing here, please review <https://github.com/clevercanyon/skeleton>.
 */

import fs from 'node:fs';
import path from 'node:path';
import { $prettier } from '../../../../../../../node_modules/@clevercanyon/utilities.node/dist/index.js';
import { $json, $str, $time } from '../../../../../../../node_modules/@clevercanyon/utilities/dist/index.js';
import u from '../../../../../resources/utilities.mjs';

/**
 * Updates `./tsconfig.json` file.
 *
 * WARNING: In this file, don't use anything from our `u` (utilities) package that resolves relative directory paths
 * and/or derives information from relative directory paths, without first calling `u.switchProjDir()` to properly
 * prepare utilities. When this file is called upon, it is passed a `projDir` explicitly. This file should only operate
 * on that project directory. Also, don't forget to `u.restoreProjDir()`, to restore the previous project directory.
 */
export default async ({ projDir }) => {
    /**
     * Switches to `projDir`.
     */
    await u.switchProjDir(projDir);

    /**
     * Initializes vars.
     */
    const tsconfigFile = path.resolve(u.projDir, './tsconfig.json');
    const prettierConfig = { ...(await $prettier.resolveConfig(u.pkgFile)), parser: 'json' };

    /**
     * Defines `./tsconfig.json` file comments.
     */
    const tsconfigFileComments = $str.dedent(`
        /**
         * Auto-generated TypeScript config file.
         *
         * TypeScript is aware of this config file's location.
         *
         * @note PLEASE DO NOT EDIT THIS FILE!
         * @note This entire file will be updated automatically.
         * @note Instead of editing here, please review \`./tsconfig.mjs\`.
         *
         * Last generated using \`./tsconfig.mjs\` ${$time.now().toProse()}.
         */
    `);

    /**
     * Defines `./tsconfig.json` file contents.
     */
    const tsconfigFileObj = (await import(path.resolve(u.projDir, './tsconfig.mjs'))).default;
    const tsconfigFileContents = await $prettier.format($json.stringify(tsconfigFileObj, { pretty: true }), prettierConfig);

    /**
     * Compiles `./tsconfig.json` file contents.
     */
    fs.writeFileSync(tsconfigFile, tsconfigFileComments + '\n' + tsconfigFileContents);

    /**
     * Restores previous project directory.
     */
    await u.restoreProjDir();
};
