/**
 * Resolve configuration.
 *
 * Vite is not aware of this config file's location.
 *
 * @note PLEASE DO NOT EDIT THIS FILE!
 * @note This entire file will be updated automatically.
 * @note Instead of editing here, please review <https://github.com/clevercanyon/skeleton>.
 *
 * @see https://vite.dev/config/shared-options.html#resolve-alias
 */

import u from '../../../../../resources/utilities.mjs';

/**
 * Configures resolve.
 *
 * @param   props Props from vite config file driver.
 *
 * @returns       Resolve configuration.
 */
export default async (/* {} */) => {
    return {
        resolveConfig: {
            alias: await u.aliases.asFindReplaceRegExps(),
            extensions: [...u.exts.onImportWithNoExtensionTry],
        },
    };
};
