import fs from 'node:fs';
import path from 'node:path';
import u from '../../../../../resources/utilities.mjs';

/**
 * Configures SSL.
 *
 * @param   props Props from vite config file driver.
 *
 * @returns       SSL configuration.
 */
export default async (/* {} */) => {
    return {
        sslKey: fs.readFileSync(path.resolve(u.__dfAssetsDir, './ssl-certs/i10e-ca-key.pem')).toString(),
        sslCrt: fs.readFileSync(path.resolve(u.__dfAssetsDir, './ssl-certs/i10e-ca-crt.pem')).toString(),
    };
};
